UPDATE Tbl_Aspecto_Base set AspectoBase = 'El cumplimento del programa de capacitación anual, establecido por la empresa, incluyendo la inducción y reinducción para los trabajadores dependientes, cooperados, en misión y contratistas.' WHERE PK_Id_Aspecto_Base = 5
UPDATE Tbl_Aspecto_Base set AspectoBase = 'La evaluación de los puestos de trabajo en el marco de los programas de vigilancia epidemiológica de la salud de los trabajadores.' WHERE PK_Id_Aspecto_Base = 6
UPDATE Tbl_Aspecto_Base set AspectoBase = 'La descripción sociodemográfica de los trabajadores y la caracterización de sus condiciones de salud, así como la evaluación y análisis de las estadísticas sobre la efermedad y la accidentadilidad.' WHERE PK_Id_Aspecto_Base = 7
UPDATE Tbl_Aspecto_Base set AspectoBase = 'Registro y seguimiento a los resultados de los indicadores definidos en el SGSST de la empresa del año inmediatamente anterior.' WHERE PK_Id_Aspecto_Base = 8